    lbu $t0, dirty
    bne $t0, 1, ENDIF_run_game_1
    IF_run_game_1: # if (dirty) 
        
        # t0 = &game
        la $t0, game
        
        # game.is_paused = 0
        li $t1, 0
        sb $t1, 2150($t0)
        
        # t1 count = 0
        li $t1, 0
        
        # if (game.mode_mask == 2) // Custom level
        lbu $t1, 2148($t0)
        bne $t1, 2, ELSE_run_game_14
        IF_run_game_46:
            # t3 = &custom + (game.level_idx) * sizeof(Level)
            la $t4, custom_levels
            lbu $t2, 2149($t0)
            li $t3, 2124  # // level size
            mult $t2, $t3
            mflo $t3
            add $t3, $t4, $t3
        
            j ENDIF_run_game_46
        ELSE_run_game_14: # else: // Regular level
        
            # t3 = &regular_levels + (game.level_idx) * sizeof(Level)
            la $t4, regular_levels
            lbu $t2, 2149($t0)
            li $t3, 2124  # // level size
            mult $t2, $t3
            mflo $t3
            add $t3, $t4, $t3
            
        ENDIF_run_game_46:
        
        
        LOOP_run_game_1: # while (count < sizeof(level))  // Copy level into game struct
            beq $t1, 2124, ENDLOOP_run_game_1
            
            # *t0 = *t3  // Copy next byte
            lbu $t5, 0($t3)
            sb $t5, 0($t0)
            
            # t0++, t3++  // Increment address to next byte
            addi $t0, $t0, 1
            addi $t3, $t3, 1
            
            # count++
            addi $t1, $t1, 1
            j LOOP_run_game_1
        ENDLOOP_run_game_1:
        
        # game->ball_pos == (60, 102), ->ball_vel = (0, 0), 
        #     ->paddle_pos = (50, 110)
        la $t0, game
        li $t1, 60
        sw $t1, 2124($t0)
        li $t1, 102
        sw $t1, 2128($t0)
        li $t1, 0         # TODO: CHANGE VELOCITY X BACK TO 0
        sw $t1, 2132($t0)   
        li $t1, 0           # TODO: REMOVE THIS LINE, VELOCITY Y CHANGE
        sw $t1, 2136($t0)
        li $t1, 50
        sw $t1, 2140($t0)
        li $t1, 110
        sw $t1, 2144($t0)
        
        # clear_screen
        jal fn_clear_screen
        
        # draw_level(&(game->level))
        la $t0, game
        sw $t0, 0($sp)
        subi $sp, $sp, 4
        jal fn_draw_level
        
        # draw_rect(white, game->paddle_pos, Vec(25, 3)) // Draw Paddle
        la $t0, game
        li $t1, 0x00ffffff
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        lw $t1, 2140($t0)
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        lw $t1, 2144($t0)
        addi $t1, $t1, 10 # // Account for topbar. Actual draw position shifts by 10
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        li $t1, 25
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        li $t1, 3
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        jal fn_draw_rect
        
        # draw_rect(gray, game->ball_pos, Vec(3, 3)) // Draw Ball
        la $t0, game
        li $t1, 0x0eeeeee
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        lw $t1, 2124($t0)
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        lw $t1, 2128($t0)
        addi $t1, $t1, 10 # // Account for topbar. Actual draw position shifts by 10
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        li $t1, 3
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        li $t1, 3
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        jal fn_draw_rect
        
        # draw_bitmap(Vec(1, 0), &BMP_SCORE) // Draw Score BMP
        li $t1, 16
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        li $t1, 0
        sw $t1, 0($sp)
        subi $sp, $sp, 4
