        subi $sp, $sp, 4
        li $t1, 2
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        la $t0, game
        lbu $t1, 2149($t0)
        addi $t1, $t1, 1
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        jal fn_draw_int
    
    
        # if (0 < game->lives):
        la $t0, game
        lw $t1, 2156($t0)
        li $t3, 0
        sltu $t2, $t3, $t1 
        bne $t2, 1, ELSE_run_game_1
        IF_run_game_2:
            # draw_bitmap(Vec(100, 0), &BMP_HEART_EMPTY) // Draw full heart
