# Given a pixel number x_shift, check if paddle will collide with a wall
# after the movement. If not, perform the movement. Otherwise, move the paddle
# as far as it can go without colliding. Only checks collision with walls. It
# is assumed that bricks will never be in the way of the paddle
fn_move_paddle: # (int x_shift) -> void
    addi $sp, $sp, 4
    lw $t0, 0($sp)
    sw $ra, 0($sp)
    subi $sp, $sp, 4
    
    # 4($sp) x_shift
    # 8($sp) i
    # 12($sp) w
    # 16($sp) ptr
    # 20($sp) is_collide
    # 24($sp) loop_bound
    subi $sp, $sp, 4
    subi $sp, $sp, 4
    subi $sp, $sp, 4
    subi $sp, $sp, 4
    subi $sp, $sp, 4
    sw $t0, 0($sp)
    subi $sp, $sp, 4
    
    # loop_bound = abs(x_shift) + 1
    lw $t1, 4($sp)
    bgez $t1, ENDIF_move_paddle_4
    IF_move_paddle_4:
        neg $t1, $t1
    ENDIF_move_paddle_4:
    addi $t1, $t1, 1
    sw $t1, 24($sp)
    
    # i = 1
    li $t1, 1
    sw $t1, 8($sp)
    
    # for i in [1, x_shift - 1, ..., x_shift]
    LOOP_move_paddle_1:
        lw $t1, 8($sp)
        lw $t2, 24($sp)
        beq $t1, $t2, ENDLOOP_move_paddle_1
    
        # is_collide = 0
        li $t1, 0
        sw $t1, 20($sp)
        
        # w = 0
        li $t1, 0
        sw $t1, 12($sp)
        
        # ptr = &(game->walls)
        la $t0, game
        addi $t0, $t0, 1924
        sw $t0, 16($sp)
        
        # for w in walls:
        LOOP_move_paddle_2:
            la $t0, game
            lbu $t1, 1($t0)
            lw $t2, 12($sp)
            beq $t1, $t2, ENDLOOP_move_paddle_2
            
            # if (w != NULL)
            lw $t1, 16($sp)
            lw $t2, 0($t1)
            srl $t3, $t2, 24
            beq $t3, 0xff, ENDIF_move_paddle_3
            IF_move_paddle_3:
            
                # if collide(w, Vec(paddle_pos.x +/- i, paddle_pos.y), Vec(25, 3))
                lw $t3, 16($sp)
                la $t0, game
                lw $t1, 2140($t0)
                
                lw $t2, 8($sp)
                lw $t8, 4($sp)
                bgez $t8, ELSE_move_paddle_2
                IF_move_paddle_6: # if (x_shift < 0)
                    sub $t1, $t1, $t2
                    j ENDIF_move_paddle_6
                ELSE_move_paddle_2: # else (x_shift >= 0)
                    add $t1, $t1, $t2
                ENDIF_move_paddle_6:
                
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 2144($t0)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                li $t1, 25
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                li $t1, 3
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 4($t3)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 8($t3)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 12($t3)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 16($t3)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                jal fn_rect_collide
                addi $sp, $sp, 4
                lw $t1, 0($sp)
                bne $t1, 1, ENDIF_move_paddle_1
                IF_move_paddle_1:
                    # is_collide = 1
                    li $t1, 1
                    sw $t1, 20($sp)
                ENDIF_move_paddle_1:
            
                # w++
                lw $t1, 12($sp)
                addi $t1, $t1, 1
                sw $t1, 12($sp)
            
            ENDIF_move_paddle_3:
            
            # ptr += sizeof(Rect)
            lw $t1, 16($sp)
            addi $t1, $t1, 20
            sw $t1, 16($sp)
            
            j LOOP_move_paddle_2
        ENDLOOP_move_paddle_2:
                
        # if is_collide
        lw $t1, 20($sp)
        bne $t1, 1, ENDIF_move_paddle_2
        IF_move_paddle_2:            
            # break
            j ENDLOOP_move_paddle_1
        ENDIF_move_paddle_2:
            
        # i++
        lw $t1, 8($sp)
        addi $t1, $t1, 1
        sw $t1, 8($sp)
            
        j LOOP_move_paddle_1
    ENDLOOP_move_paddle_1:
    
    # i -= 1
    lw $t1, 8($sp)
    subi $t1, $t1, 1
    sw $t1, 8($sp)

    # Erase paddle
    la $t0, game
    lw $t1, BG_COLOR
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    lw $t1, 2140($t0)
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    lw $t1, 2144($t0)
    addi $t1, $t1, 10 # // topbar shift
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 25
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 3
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    jal fn_draw_rect
    
    # paddle_pos.x += i if x_shift > 0 else -= i
    la $t0, game
    lw $t1, 2140($t0)
    lw $t2, 8($sp)
    lw $t3, 4($sp)
    
    bgez $t3, ELSE_move_paddle_1
    IF_move_paddle_5: # if (x_shift < 0)
        sub $t1, $t1, $t2
        j ENDIF_move_paddle_5
    ELSE_move_paddle_1: # else (x_shift >= 0)
        add $t1, $t1, $t2
    ENDIF_move_paddle_5:
    sw $t1, 2140($t0)
    
    # Draw paddle
    la $t0, game
    li $t1, 0x00ffffff
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    lw $t1, 2140($t0)
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    lw $t1, 2144($t0)
    addi $t1, $t1, 10 # // topbar shift
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 25
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 3
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    jal fn_draw_rect
    
    
    # return
