# Draws the level whose first byte is at the address specified by ptr
fn_draw_level: # (int ptr) -> void
    subi $sp, $sp, 4
    sw $ra, 0($sp)
    subi $sp, $sp, 4
    subi $sp, $sp, 4
    subi $sp, $sp, 4
    # 4($sp) obj_count
    # 8($sp) i    // Valid object iteration count
    # 12($sp) = link
    # 16($sp) = j   // Total iteration count
    # 20($sp) ptr
    
    # if (! ptr->is_null)
    lw $t0, 20($sp)
    lbu $t1, 3($t0)
    beq $t1, 1, ENDIF_draw_level_1
    IF_draw_level_1:
        
        # i = 0
        li $t0, 0
        sw $t0, 8($sp)
        
        # j = 0
        li $t0, 0
        sw $t0, 16($sp)
        
        # obj_count = ptr->num_bricks
        lw $t0, 20($sp)
        lbu $t1, 2($t0)
        sw $t1, 4($sp)
        
        LOOP_draw_level_1: # while i < obj_count
            lw $t0, 8($sp)
            lw $t1, 4($sp)
            beq $t0, $t1, ENDLOOP_draw_level_1
        
            # t0 = &(ptr->bricks)
            lw $t0, 20($sp)
            addi $t0, $t0, 4
            
            # t0 += j * sizeof(Brick)
            lw $t1, 16($sp) # // i
            li $t2, 24  # // Brick size
            mult $t1, $t2
            mflo $t1
            add $t0, $t0, $t1
            
            # if (t0->life != 0)
            lw $t1, 0($t0)
            beq $t1, 0, ENDIF_draw_level_2
            IF_draw_level_2:
                
                # draw_rect(t0->rect.color, t0->rect.pos, t0->rect.w_h) // Draw brick
                lw $t1, 4($t0)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 8($t0)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 12($t0)
                addi $t1, $t1, 10 # // Shift 10 to account for topbar
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 16($t0)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 20($t0)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                jal fn_draw_rect
                
                # i++
                lw $t0, 8($sp)
                addi $t0, $t0, 1
                sw $t0, 8($sp)
                
            ENDIF_draw_level_2:
            
            # j++
            lw $t0, 16($sp)
            addi $t0, $t0, 1
            sw $t0, 16($sp)
        
            j LOOP_draw_level_1
        ENDLOOP_draw_level_1:
        
        # i = 0
        li $t0, 0
        sw $t0, 8($sp)
        
        # j = 0
        li $t0, 0
        sw $t0, 16($sp)
        
        # obj_count = ptr->num_walls
        lw $t0, 20($sp)
        lbu $t1, 1($t0)
        sw $t1, 4($sp)
        
        LOOP_draw_level_2: # while i < obj_count
            lw $t0, 8($sp)
            lw $t1, 4($sp)
            beq $t0, $t1, ENDLOOP_draw_level_2
        
            # t0 = &(ptr->walls)
            lw $t0, 20($sp)
            addi $t0, $t0, 1924
            
            # t0 += j * sizeof(Rect)
            lw $t1, 16($sp) # // i
            li $t2, 20  # // Rect (Wall) size
            mult $t1, $t2
            mflo $t1
            add $t0, $t0, $t1
            
            # if (t0->color < ff000000)
            lw $t1, 0($t0)
            sltu $t1, $t1, 0xff000000
            beq $t1, 0, ENDIF_draw_level_3
            IF_draw_level_3:
                
                # draw_rect(t0->color, t0->pos, t0->w_h) // Draw brick
                lw $t1, 0($t0)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 4($t0)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 8($t0)
                addi $t1, $t1, 10 # // Shift 10 to account for topbar
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 12($t0)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                lw $t1, 16($t0)
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                jal fn_draw_rect
                
                # i++
                lw $t0, 8($sp)
                addi $t0, $t0, 1
                sw $t0, 8($sp)
                
            ENDIF_draw_level_3:
            
            # j++
            lw $t0, 16($sp)
            addi $t0, $t0, 1
            sw $t0, 16($sp)
        
            j LOOP_draw_level_2
        ENDLOOP_draw_level_2:
        
    ENDIF_draw_level_1:
    
    # return
    addi $sp, $sp, 4
    addi $sp, $sp, 4
    addi $sp, $sp, 4
    lw $ra, 0($sp)
    addi $sp, $sp, 4
    addi $sp, $sp, 4
    jr $ra



