# Convert an (x,y) Vec struct to a a display address
fn_vec2addr: # (Vec) -> int
    # x, y = Vec
    addi $sp, $sp, 4
    lw $t1, 0($sp)
    addi $sp, $sp, 4
    lw $t0, 0($sp)
    
    li $t2, 0 # cur_addr = 0
    
    # x_shift = 4 * x
    li $t4, 4
    mult $t0, $t4
    mflo $t4
    
    # cur_addr += x_shift
    add $t2, $t2, $t4
    
    # y_shift = 4 * 128 * y
    lw $t5, SCREEN_WIDTH 
    li $t4, 4
    mult $t5, $t4
    mflo $t4 # // t4 = 4 * 128, bytes to shift a whole display row
    mult $t4, $t1
    mflo $t4
    
    # cur_addr += y_shift
    add $t2, $t2, $t4
    
    # return t0
    sw $t2, 0($sp)
    subi $sp, $sp, 4
    jr $ra
    
    
    
    
# Given value, draw base 10 integer (supports multidigit) using addr as topleft
# Precondition: val has <= 9 digits
fn_draw_int: # (Vec top_left, int val) -> void
    # t0 x, t1 y, t2 val = addr, val
    addi $sp, $sp 4
    lw $t2, 0($sp)
    addi $sp, $sp, 4
    lw $t1, 0($sp)
    addi $sp, $sp 4
    lw $t0, 0($sp)
    
    # if (val == 0)
    bne $t2, 0, ENDIF_fn_draw_int_0
    IF_fn_draw_int_0:
        # draw_bitmap(Vec(x,y), &BMP_ZERO)
        sw $ra, 0($sp)
        subi $sp, $sp, 4
        sw $t0, 0($sp)
        subi $sp, $sp, 4
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        la $t9, BMP_ZERO
        sw $t9, 0($sp)
        subi $sp, $sp, 4
        jal fn_draw_bitmap
        addi $sp, $sp, 4
        lw $ra, 0($sp)
    
        # return
        j ENDLOOP_fn_draw_int_2
    ENDIF_fn_draw_int_0:

    # t4 counter = 0
    li $t4, 0
    
    LOOP_fn_draw_int_1: # while (val != 0)
        beq $t2, 0, ENDLOOP_fn_draw_int_1
        
        # t3 digit = val % 10
        # t2 val = val // 10
        li $t3, 10
        div $t2, $t3
        mflo $t2
        mfhi $t3
        
        # stack.push(digit)
        sw $t3, 0($sp)
        subi $sp, $sp, 4
        
        # counter++
        addi $t4, $t4, 1
        
        j LOOP_fn_draw_int_1
    ENDLOOP_fn_draw_int_1:
    
    LOOP_fn_draw_int_2: # while (counter > 0)
        beq $t4, 0, ENDLOOP_fn_draw_int_2
        
        # t3 digit = stack.pop()
        addi $sp, $sp, 4
        lw $t3, 0($sp)
        
        beq $t3, 0, IF_fn_draw_int_1
        beq $t3, 1, ELIF_fn_draw_int_1
        beq $t3, 2, ELIF_fn_draw_int_2
        beq $t3, 3, ELIF_fn_draw_int_3
        beq $t3, 4, ELIF_fn_draw_int_4
        beq $t3, 5, ELIF_fn_draw_int_5
        beq $t3, 6, ELIF_fn_draw_int_6
        beq $t3, 7, ELIF_fn_draw_int_7
        beq $t3, 8, ELIF_fn_draw_int_8
        beq $t3, 9, ELIF_fn_draw_int_9
        IF_fn_draw_int_1: # if digit == 0
            la $t5, BMP_ZERO # t5 BitMap map = BMP_ZERO
            j ENDIF_fn_draw_int_1
        ELIF_fn_draw_int_1: # elif digit == 1
            la $t5, BMP_ONE # BitMap map = BMP_ONE
            j ENDIF_fn_draw_int_1
        ELIF_fn_draw_int_2: # elif digit == 2
            la $t5, BMP_TWO # BitMap map = BMP_TWO
            j ENDIF_fn_draw_int_1
        ELIF_fn_draw_int_3: # elif digit == 3
            la $t5, BMP_THREE # BitMap map = BMP_THREE
            j ENDIF_fn_draw_int_1
        ELIF_fn_draw_int_4: # elif digit == 4
            la $t5, BMP_FOUR # BitMap map = BMP_FOUR
            j ENDIF_fn_draw_int_1
        ELIF_fn_draw_int_5: # elif digit == 5
            la $t5, BMP_FIVE # BitMap map = BMP_FIVE
            j ENDIF_fn_draw_int_1
        ELIF_fn_draw_int_6: # elif digit == 6
            la $t5, BMP_SIX # BitMap map = BMP_SIX
            j ENDIF_fn_draw_int_1
        ELIF_fn_draw_int_7: # elif digit == 7
            la $t5, BMP_SEVEN # BitMap map = BMP_SEVEN
            j ENDIF_fn_draw_int_1
        ELIF_fn_draw_int_8: # elif digit == 8
            la $t5, BMP_EIGHT # BitMap map = BMP_EIGHT
            j ENDIF_fn_draw_int_1
        ELIF_fn_draw_int_9: # elif digit == 9
            la $t5, BMP_NINE # BitMap map = BMP_NINE
            j ENDIF_fn_draw_int_1
        ENDIF_fn_draw_int_1:
        
        # t6 i, t7 j = 0
        li $t6, 0
        li $t7, 0
        
        # t2 width = map.width
        # t3 height = map.height
        lw $t2, 0($t5)
        lw $t3, 4($t5)
        
        # map = &map.pixels
        addi $t5, $t5, 8
        
        LOOP_fn_draw_int_3: # while (j != height)
            beq $t7, $t3, ENDLOOP_fn_draw_int_3
            
            # i = 0
            li $t6, 0
        
            LOOP_fn_draw_int_4: # while (i != height)
                beq $t6, $t2, ENDLOOP_fn_draw_int_4
                
                # if (*map != 0xff******)
                lw $t8, 0($t5)
                srl $t8, $t8, 24
                beq $t8, 0xff, ENDIF_fn_draw_int_2
                IF_fin_draw_int_2:
                    # t8 cur_x = x + i
                    # t9 cur_y = y + j
                    add $t8, $t0, $t6
                    add $t9, $t1, $t7
                    
                    # stack.push(t0 x, t1, y, t2 width, t3 height, t4 counter, t5 map, t6 i, t7 j)
                    sw $t0, 0($sp)
                    subi $sp, $sp, 4
                    sw $t1, 0($sp)
                    subi $sp, $sp, 4
                    sw $t2, 0($sp)
                    subi $sp, $sp, 4
                    sw $t3, 0($sp)
                    subi $sp, $sp, 4
                    sw $t4, 0($sp)
                    subi $sp, $sp, 4
                    sw $t5, 0($sp)
                    subi $sp, $sp, 4
                    sw $t6, 0($sp)
                    subi $sp, $sp, 4
                    sw $t7, 0($sp)
                    subi $sp, $sp, 4
                    
                    # t8 addr = vec2addr(Vec(cur_x, cur_y))
                    sw $ra, 0($sp)
                    subi $sp, $sp, 4
                    sw $t8, 0($sp)
                    subi $sp, $sp, 4
                    sw $t9, 0($sp)
                    subi $sp, $sp, 4
                    jal fn_vec2addr
                    addi $sp, $sp, 4
                    lw $t8, 0($sp)
                    addi $sp, $sp, 4
                    lw $ra, 0($sp)
                    
                    # repeat(7, stack.pop())
                    addi $sp, $sp, 4
                    lw $t7, 0($sp)
                    addi $sp, $sp, 4
                    lw $t6, 0($sp)
                    addi $sp, $sp, 4
                    lw $t5, 0($sp)
                    addi $sp, $sp, 4
                    lw $t4, 0($sp)
                    addi $sp, $sp, 4
                    lw $t3, 0($sp)
                    addi $sp, $sp, 4
                    lw $t2, 0($sp)
                    addi $sp, $sp, 4
                    lw $t1, 0($sp)
                    addi $sp, $sp, 4
                    lw $t0, 0($sp)
                    
                    # display[addr] = *map
                    lw $t9, ADDR_DSPL
                    add $t9, $t9, $t8
                    lw $t8, 0($t5)
                    sw $t8, 0($t9)
                ENDIF_fn_draw_int_2:
                
                # map++
                addi $t5, $t5, 4
                
                # i++
                addi $t6, $t6, 1
                j LOOP_fn_draw_int_4
            ENDLOOP_fn_draw_int_4:
            
            # j++
            addi $t7, $t7, 1
            j LOOP_fn_draw_int_3
        ENDLOOP_fn_draw_int_3:
        
        # x += width + 2
        add $t0, $t0, $t2
        addi $t0, $t0, 2
        
        # counter--
        subi $t4, $t4, 1
        
        j LOOP_fn_draw_int_2
    ENDLOOP_fn_draw_int_2:
    
    # return
    jr $ra
    
    

# Draws hollow rectangle
fn_draw_hollow_rect: # (Rect rect) -> void
    # 8($sp) h
    # 12($sp) w
    # 16($sp) y
    # 20($sp) x
    # 24($sp) color
    sw $ra, 0($sp)
    subi $sp, $sp, 4
    
    # // Top line
    lw $t0, 20($sp) # x
    lw $t1, 16($sp) # y
    lw $t2, 12($sp) # w
    lw $t7, 24($sp) # color
    sw $t7, 0($sp)
    subi $sp, $sp, 4
    sw $t0, 0($sp)
    subi $sp, $sp, 4
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    sw $t2, 0($sp)
    subi $sp, $sp, 4
    li $t1, 1
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    jal fn_draw_rect
    
    # // Left Line
    lw $t0, 20($sp) # x
    lw $t1, 16($sp) # y
    lw $t2, 8($sp) # h
    lw $t7, 24($sp) # color
    sw $t7, 0($sp)
    subi $sp, $sp, 4
    sw $t0, 0($sp)
    subi $sp, $sp, 4
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 1
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    sw $t2, 0($sp)
    subi $sp, $sp, 4
    jal fn_draw_rect
    
    # // Bottom line
    lw $t0, 20($sp) # x
    lw $t1, 16($sp) # y
    lw $t2, 12($sp) # w
    lw $t3, 8($sp) # h
    add $t1, $t1, $t3 # y + h
    lw $t7, 24($sp) # color
    sw $t7, 0($sp)
    subi $sp, $sp, 4
    sw $t0, 0($sp)
    subi $sp, $sp, 4
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    sw $t2, 0($sp)
    subi $sp, $sp, 4
    li $t1, 1
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    jal fn_draw_rect
    
    # // Right Line
    lw $t0, 20($sp) # x
    lw $t1, 16($sp) # y
    lw $t2, 8($sp) # h
    lw $t3, 12($sp) # w
    add $t0, $t0, $t3 # x + w
    lw $t7, 24($sp) # color
    sw $t7, 0($sp)
    subi $sp, $sp, 4
    sw $t0, 0($sp)
    subi $sp, $sp, 4
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 1
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    sw $t2, 0($sp)
    subi $sp, $sp, 4
    jal fn_draw_rect
    
    # return
    addi $sp, $sp, 4
    lw $ra, 0($sp)
    addi $sp, $sp, 20  # Pop rect params
    jr $ra
    
    
    
# Fill a rectangular region of the screen with a color
fn_draw_rect: # (Rect rect) -> void
    # t0 x, t1 y = rect.pos
    # t2 w, t3 h = rect.w_h
    # t6 col = rect.color
    addi $sp, $sp, 4
    lw $t3, 0($sp)
    addi $sp, $sp, 4
    lw $t2, 0($sp)
    addi $sp, $sp, 4
    lw $t1, 0($sp)
    addi $sp, $sp, 4
    lw $t0, 0($sp)
    addi $sp, $sp, 4
    lw $t6 0($sp)
    
    # t4 i, t5 j = 0
    li $t4, 0
    li $t5, 0
    
    LOOP_fn_erase_1: # while (j < h)
        beq $t5, $t3, ENDLOOP_fn_erase_1 
    
        # i = 0
        li $t4, 0
        
        LOOP_fn_erase_2: # while (i < w)
            beq $t4, $t2, ENDLOOP_fn_erase_2
        
            # t8 cur_x = x + i
            # t9 cur_y = y + j
            add $t8, $t0, $t4
            add $t9, $t1, $t5
            
            # stack.push(t0 x, t1, y, t2 w, t3 h, t4 i, t5 j, t6 col)
            sw $t0, 0($sp)
            subi $sp, $sp, 4
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            sw $t2, 0($sp)
            subi $sp, $sp, 4
            sw $t3, 0($sp)
            subi $sp, $sp, 4
            sw $t4, 0($sp)
            subi $sp, $sp, 4
            sw $t5, 0($sp)
            subi $sp, $sp, 4
            sw $t6, 0($sp)
            subi $sp, $sp, 4
            
            # t8 addr = vec2addr(Vec(cur_x, cur_y))
            sw $ra, 0($sp)
            subi $sp, $sp, 4
            sw $t8, 0($sp)
            subi $sp, $sp, 4
            sw $t9, 0($sp)
            subi $sp, $sp, 4
            jal fn_vec2addr
            addi $sp, $sp, 4
            lw $t8, 0($sp)
            addi $sp, $sp, 4
            lw $ra, 0($sp)
            
            # repeat(7, stack.pop())
            addi $sp, $sp, 4
            lw $t6, 0($sp)
            addi $sp, $sp, 4
            lw $t5, 0($sp)
            addi $sp, $sp, 4
            lw $t4, 0($sp)
            addi $sp, $sp, 4
            lw $t3, 0($sp)
            addi $sp, $sp, 4
            lw $t2, 0($sp)
            addi $sp, $sp, 4
            lw $t1, 0($sp)
            addi $sp, $sp, 4
            lw $t0, 0($sp)
        
            # if (! addr out of bounds): display[addr] = *map
            lw $t9, ADDR_DSPL
            add $t9, $t9, $t8
            bgt $t8, 65532, ENDIF_fn_erase_1
            IF_fn_erase_1:
                sw $t6, 0($t9)
            ENDIF_fn_erase_1:
        
            # i++
            addi $t4, $t4, 1
            j LOOP_fn_erase_2
        ENDLOOP_fn_erase_2:
        
        # j++
        addi $t5, $t5, 1
        j LOOP_fn_erase_1
    ENDLOOP_fn_erase_1:
    
    # return
    jr $ra
    
    
# Draw bitmap at given address using addr as topleft of display
fn_draw_bitmap: # (Vec top_left, int bitmap_addr) -> void
    subi $sp, $sp, 4
    subi $sp, $sp, 4
    subi $sp, $sp, 4
    subi $sp, $sp, 4
    # 4($sp) i
    # 8($sp) j
    # 12($sp) w
    # 16($sp) h
    # 20($sp) bmp_addr
    # 24($sp) y
    # 28($sp) x
    
    # i, j = 0
    li $t0, 0
    sw $t0, 4($sp)
    sw $t0, 8($sp)
    
    # w, h = bmp_addr->width, bmp_addr->height
    lw $t0, 20($sp)
    lw $t1, 0($t0)
    lw $t2, 4($t0)
    sw $t1, 12($sp)
    sw $t2, 16($sp)
    
    # bitmap_addr += 8
    lw $t0, 20($sp)
    addi $t0, $t0, 8
    sw $t0, 20($sp)
    
    LOOP_fn_draw_bitmap_1: # while (j < h)
        lw $t0, 8($sp)
        lw $t1, 16($sp) 
        beq $t0, $t1, ENDLOOP_fn_draw_bitmap_1
    
        # i = 0
        li $t0, 0
        sw $t0, 4($sp)
    
        LOOP_fn_draw_bitmap_2: # while (i < w)
            lw $t0, 4($sp)
            lw $t1, 12($sp) 
            beq $t0, $t1, ENDLOOP_fn_draw_bitmap_2
            
            # if (*bitmap_addr != 0xff******)
            lw $t0, 20($sp)
            lw $t1, 0($t0)
            srl $t1, $t1, 24
            beq $t1, 0xff, ENDIF_fn_draw_bitmap_1
            IF_fn_draw_bitmap_1:
            
                # t8 cur_x, t9 cur_y = x + i, y + j
                lw $t0, 28($sp)
                lw $t1, 24($sp)
                lw $t2, 4($sp)
                lw $t3, 8($sp)
                add $t8, $t0, $t2
                add $t9, $t1, $t3
                
                # t0 addr = vec2addr(Vec(cur_x, cur_y))
                sw $ra, 0($sp)
                subi $sp, $sp, 4
                sw $t8, 0($sp)
                subi $sp, $sp, 4
                sw $t9, 0($sp)
                subi $sp, $sp, 4
                jal fn_vec2addr
                addi $sp, $sp, 4
                lw $t0, 0($sp)
                addi $sp, $sp, 4
                lw $ra, 0($sp)
            
                # if (0 <= addr <= 65532)
                sle $t6, $t0, 65532
                sge $t5, $t0, 0
                and $t5, $t5, $t6
                bne $t5, 1, ENDIF_fn_draw_bitmap_2
                IF_fn_draw_bitmap_2:
                    # display[addr] = *bitmap_addr
                    lw $t1, ADDR_DSPL
                    add $t1, $t1, $t0
                    lw $t2, 20($sp)
                    lw $t3, 0($t2)
                    sw $t3, 0($t1)
                ENDIF_fn_draw_bitmap_2:
                
            ENDIF_fn_draw_bitmap_1:
            
            # bitmap_addr += 4
            lw $t0, 20($sp)
            addi $t0, $t0, 4
            sw $t0, 20($sp)
        
            # i++
            lw $t0, 4($sp)
            addi $t0, $t0, 1
            sw $t0, 4($sp)
        
            j LOOP_fn_draw_bitmap_2
        ENDLOOP_fn_draw_bitmap_2:
        
        # j++
        lw $t0, 8($sp)
        addi $t0, $t0, 1
        sw $t0, 8($sp)
    
        j LOOP_fn_draw_bitmap_1
    ENDLOOP_fn_draw_bitmap_1:
    
    # // pop entire stack frame
    addi $sp, $sp, 4
    addi $sp, $sp, 4
    addi $sp, $sp, 4
    addi $sp, $sp, 4
    addi $sp, $sp, 4
    addi $sp, $sp, 4
    addi $sp, $sp, 4
    
    # return
    jr $ra
    
    
# Return 1 if rects collide, else 0
fn_rect_collide: # (Vec r1_tl, Vec r1_wh, Vec r2_tl, Vec r2_wh) -> int
    # t0 = r1.x
    # t1 = r1.w
    # t2 = r1.y
    # t3 = r1.h
    # t4 = r2.x
    # t5 = r2.w
    # t6 = r2.y 
    # t7 = r2.h
    addi $sp, $sp, 4
    lw $t7, 0($sp)
    addi $sp, $sp, 4
    lw $t5, 0($sp)
    addi $sp, $sp, 4
    lw $t6, 0($sp)
    addi $sp, $sp, 4
    lw $t4, 0($sp)
    addi $sp, $sp, 4
    lw $t3, 0($sp)
    addi $sp, $sp, 4
    lw $t1, 0($sp)
    addi $sp, $sp, 4
    lw $t2, 0($sp)
    addi $sp, $sp, 4
    lw $t0, 0($sp)
    
    # t1 = r1.x + r1.w
    add $t1, $t0, $t1
    # t3 = r1.y + r1.h
    add $t3, $t2, $t3
    # t5 = r2.x + r2.w
    add $t5, $t4, $t5
    # t7 = r2.y + r2.h
    add $t7, $t6, $t7
    
    # s7 = ( (t4 < t1 < t5 || t4 < t0 < t5 || (t1 == t5 && t0 == t4)) && (t6 < t2 <= t7 || t6 < t3 < t7) || (t3 == t7 && t2 == t6))
    slt $t8, $t1, $t5 # // t8 = t1 < t5 
    slt $t9, $t4, $t1 # // t9 = t4 < t1
    and $t8, $t8, $t9 # // t8 = t4 < t1 < t5
    slt $t9, $t0, $t5 # // t9 = t0 < t5
    slt $s0, $t4, $t0 # // s0 = t4 < t0
    and $t9, $t9, $s0 # // t9 = t4 < t0 < t5
    or $t8, $t8, $t9 # // t8 = t4 < t1 < t5 || t4 < t0 < t5
    
    seq $t9, $t1, $t5 # // t9 = t1 == t5
    seq $s0, $t0, $t4 # // s0 = t0 == t4
    and $t9, $t9, $s0 # // t9 = t1 == t5 && t0 == t4
    or $t8, $t8, $t9 # // t8 = t4 < t1 < t5 || t4 < t0 < t5 || (t1 == t5 && t0 == t4)
    
    slt $t9, $t6, $t2 # // t9 = t6 < t2
    slt $s0, $t2, $t7 # // s0 = t2 < t7
    and $t9, $t9, $s0 # // t9 = t6 < t2 < t7
    slt $s0, $t6, $t3 # // s0 = t6 < t3
    slt $s1, $t3, $t7 # // s1 = t3 < t7
    and $s0, $s0,$s1 # // s0 = t6 < t3 < t7
    or $t9, $t9, $s0 # // t9 = t6 < t2 < t7 || t6 < t3 < t7
    
    seq $s0, $t3, $t7 # // s0 = t3 == t7
    seq $s1, $t2, $t6 # // s1 = t2 == t6
    and $s0, $s0, $s1 # // s0 = t3 == t7 && t2 == t6
    or $t9, $t9, $s0 # // t9 = t6 < t2 < t7 || t6 < t3 < t7 || (t3 == t7 && t2 == t6)
    
    and $s7, $t8, $t9 # // s7 = (t4 <= t1 <= t5 || t4 <= t0 <= t5) && (t6 <= t2 <= t7 || t6 <= t3 <= t7)
    
    # // Swap rectangles. We need to check both ways, otherwise case where r2 inside r1 returns false
    # t0 <=> t4
    addi $t8, $t0, 0
    addi $t0, $t4, 0
    addi $t4, $t8, 0
    # t1 <=> t5
    addi $t8, $t1, 0
    addi $t1, $t5, 0
    addi $t5, $t8, 0
    # t2 <=> t6
    addi $t8, $t2, 0
    addi $t2, $t6, 0
    addi $t6, $t8, 0
    # t3 <=> t7
    addi $t8, $t3, 0
    addi $t3, $t7, 0
    addi $t7, $t8, 0
    
    # s6 = ( (t4 < t1 < t5 || t4 < t0 < t5 || (t1 == t5 && t0 == t4)) && (t6 < t2 <= t7 || t6 < t3 < t7) || (t3 == t7 && t2 == t6))
    slt $t8, $t1, $t5 # // t8 = t1 < t5 
    slt $t9, $t4, $t1 # // t9 = t4 < t1
    and $t8, $t8, $t9 # // t8 = t4 < t1 < t5
    slt $t9, $t0, $t5 # // t9 = t0 < t5
    slt $s0, $t4, $t0 # // s0 = t4 < t0
    and $t9, $t9, $s0 # // t9 = t4 < t0 < t5
    or $t8, $t8, $t9 # // t8 = t4 < t1 < t5 || t4 < t0 < t5
    
    seq $t9, $t1, $t5 # // t9 = t1 == t5
    seq $s0, $t0, $t4 # // s0 = t0 == t4
    and $t9, $t9, $s0 # // t9 = t1 == t5 && t0 == t4
    or $t8, $t8, $t9 # // t8 = t4 < t1 < t5 || t4 < t0 < t5 || (t1 == t5 && t0 == t4)
    
    slt $t9, $t6, $t2 # // t9 = t6 < t2
    slt $s0, $t2, $t7 # // s0 = t2 < t7
    and $t9, $t9, $s0 # // t9 = t6 < t2 < t7
    slt $s0, $t6, $t3 # // s0 = t6 < t3
    slt $s1, $t3, $t7 # // s1 = t3 < t7
    and $s0, $s0,$s1 # // s0 = t6 < t3 < t7
    or $t9, $t9, $s0 # // t9 = t6 < t2 < t7 || t6 < t3 < t7
    
    seq $s0, $t3, $t7 # // s0 = t3 == t7
    seq $s1, $t2, $t6 # // s1 = t2 == t6
    and $s0, $s0, $s1 # // s0 = t3 == t7 && t2 == t6
    or $t9, $t9, $s0 # // t9 = t6 < t2 < t7 || t6 < t3 < t7 || (t3 == t7 && t2 == t6)
    
    and $s6, $t8, $t9 # // s6 = (t4 <= t1 <= t5 || t4 <= t0 <= t5) && (t6 <= t2 <= t7 || t6 <= t3 <= t7)
    
    # if (collide either way)
    or $t8, $s6, $s7
    beq $t8, 0, ELSE_rect_collide_1
    IF_rect_collide_1:
        # return 1
        li $t9, 1
        sw $t9, 0($sp)
        subi $sp, $sp, 4
        jr $ra
        
        j ENDIF_rect_collide_1
    ELSE_rect_collide_1:
        # return 0
        li $t9, 0
        sw $t9, 0($sp)
        subi $sp, $sp, 4
        jr $ra
    ENDIF_rect_collide_1:
    
    
    
# Given a float value shifted left by shift, round the value
# up or down based on the shift
# e.g 1.899 with a shift of 10 becomes 1945
# so round(1945, 10) = 2
fn_round: # (int value, int shift) -> int
    # t0 value
    # t1 shift
    addi $sp, $sp, 4
    lw $t1, 0($sp)
    addi $sp, $sp, 4
    lw $t0, 0($sp)
    
    # t2 lower_bound_unshifted = floor(value)  (unshifted)
    srav $t2, $t0, $t1
    # t3 lower_bound_shifted = floor(value)   (shifted)
    sllv $t3, $t2, $t1
    
    # if (value == lower_bound_shifted)
    bne $t3, $t0, ENDIF_fn_round_1
    IF_fn_round_1:
        # return lower_bound_unshifted
        sw $t2, 0($sp)
        subi $sp, $sp, 4
        jr $ra
    ENDIF_fn_round_1:
    
    # t4 upper_bound_unshifted = floor(value)  (unshifted)
    addi $t4, $t2, 1
    # t5 upper_bound_shifted = floor(value)  (shifted)
    sllv $t5, $t4, $t1
    
    # t6 diff_upper = abs(value - upper_bound_shifted)
    sub $t6, $t0, $t5
    bgtz $t6, ENDIF_fn_round_2
    IF_fn_round_2:
        neg $t6, $t6
    ENDIF_fn_round_2:
    
    # t7 diff_lower = abs(value - lower_bound_shifted)
    sub $t7, $t0, $t3
    bgtz $t7, ENDIF_fn_round_3
    IF_fn_round_3:
        neg $t7, $t7
    ENDIF_fn_round_3:
    
    ble $t6, $t7, ElSE_fn_round_1
    IF_fn_round_4: # if (diff_upper > diff_lower)
        # return lower_bound_unshifted
        sw $t2, 0($sp)
        subi $sp, $sp, 4
        jr $ra
    
        j ENDIF_fn_round_4
    ElSE_fn_round_1: # else // diff_upper <= diff_lower
        # return upper_bound_unshifted
        sw $t4, 0($sp)
        subi $sp, $sp, 4
        jr $ra
        
    ENDIF_fn_round_4:



# loop through custom_levels and return the index of the first NULL level. 5 if none are null
fn_first_null_custom: # () -> int
    # t0 i = 0
    li $t0, 0
    
    # while (i < 5)
    LOOP_first_null_custom:
        beq $t0, 5, ENDLOOP_first_null_custom
    
        la $t1, custom_levels
        li $t2, 2124 # // t2 = i * sizeof(level)
        mult $t0, $t2
        mflo $t2
        add $t1, $t1, $t2
        lbu $t3, 3($t1) # // is_null
        
        # if is_null: break
        beq $t3, 1, ENDLOOP_first_null_custom
    
        # i++
        addi $t0, $t0, 1
    
        j LOOP_first_null_custom
    ENDLOOP_first_null_custom:
   
    # return
    sw $t0, 0($sp)
    subi $sp, $sp, 4
    jr $ra



# Loop through the level and check if the provided rect collides with any walls or bricks
# also check collision with default starting ball and paddle location
# The second returned int has the following values:
# 1 = collided with brick
# 2 = collided with wall
# 3 = collided with paddle or ball
# 4 = no collision
# If 1 or 2, then the first return value is a pointer to the object that was collided with
# If 3 or 4, the value of the returned pointer is irrelevant and can be garbage
fn_collide_level: # (Level *level_ptr, Vec top_left, Vec, w_h) -> void *, int
    addi $sp, $sp, 4
    lw $t4, 0($sp) # w_h.y
    addi $sp, $sp, 4
    lw $t3, 0($sp) # w_h.x
    addi $sp, $sp, 4
    lw $t2, 0($sp) # top_left.y
    addi $sp, $sp, 4
    lw $t1, 0($sp) # top_left.x
    addi $sp, $sp, 4
    lw $t0, 0($sp) # level_ptr
    
    sw $ra, 0($sp)
    subi $sp, $sp, 4
    
    # 4($sp) level_ptr
    # 8($sp) top_left.x
    # 12($sp) top_left.y
    # 16($sp) w_h.x
    # 20($sp) w_h.y
    # 24($sp) ptr // For looping bricks and walls
    # 28($sp) i // obj count loop var
    subi $sp, $sp, 4
    subi $sp, $sp, 4
    sw $t4, 0($sp)
    subi $sp, $sp, 4
    sw $t3, 0($sp)
    subi $sp, $sp, 4
    sw $t2, 0($sp)
    subi $sp, $sp, 4
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    sw $t0, 0($sp)
    subi $sp, $sp, 4
    
    # i = 0, ptr = &level_ptr->bricks
    li $t1, 0
    sw $t1, 28($sp)
    lw $t0, 4($sp)
    addi $t0, $t0, 4
    sw $t0, 24($sp)
    
    # for brick in bricks
    LOOP_collide_level_1:
        lw $t0, 4($sp) # level_ptr
        lbu $t1, 2($t0) # num_bricks
        lw $t2, 28($sp) # i
        beq $t1, $t2, ENDLOOP_collide_level_1
    
        # if (brick != NULL)
        lw $t0, 24($sp) # ptr
        lw $t1, 0($t0)
        beq $t1, 0, ENDIF_collide_level_1
        IF_collide_level_1:
            
            lw $t0, 8($sp)
            lw $t1, 12($sp)
            lw $t2, 16($sp)
            lw $t3, 20($sp)
            lw $t4, 24($sp) # ptr
            
            sw $t0, 0($sp)
            subi $sp, $sp, 4
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            sw $t2, 0($sp)
            subi $sp, $sp, 4
            sw $t3, 0($sp)
            subi $sp, $sp, 4
            
            lw $t1, 8($t4)
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            lw $t1, 12($t4)
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            lw $t1, 16($t4)
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            lw $t1, 20($t4)
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            jal fn_rect_collide
            addi $sp, $sp, 4
            lw $t0, 0($sp) # t0 = if brick collided with rect
            
            bne $t0, 1, ENDIF_collide_level_3
            IF_collide_level_3:
                # return
                lw $t3, 24($sp)
                
                addi $sp, $sp, 28 # // Pop local vars
                addi $sp, $sp, 4
                lw $ra, 0($sp)
                
                sw $t3, 0($sp)
                subi $sp, $sp, 4
                li $t1, 1
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                jr $ra
            ENDIF_collide_level_3:
            
            # i++
            lw $t1, 28($sp)
            addi $t1, $t1, 1
            sw $t1, 28($sp)
            
        ENDIF_collide_level_1:
        
        # ptr += sizeof(Brick) // 24
        lw $t1, 24($sp)
        addi $t1, $t1, 24
        sw $t1, 24($sp)
    
        j LOOP_collide_level_1
    ENDLOOP_collide_level_1:
    
    # i = 0, ptr = &level_ptr->walls
    li $t1, 0
    sw $t1, 28($sp)
    lw $t0, 4($sp)
    addi $t0, $t0, 1924
    sw $t0, 24($sp)
    
    # for wall in walls
    LOOP_collide_level_2:
        lw $t0, 4($sp) # level_ptr
        lbu $t1, 1($t0) # num_walls
        lw $t2, 28($sp) # i
        beq $t1, $t2, ENDLOOP_collide_level_2
    
        # if (wall != NULL)
        lw $t0, 24($sp) # ptr
        lw $t1, 0($t0)
        srl $t1, $t1, 24
        beq $t1, 0xff, ENDIF_collide_level_2
        IF_collide_level_2:
            
            lw $t0, 8($sp)
            lw $t1, 12($sp)
            lw $t2, 16($sp)
            lw $t3, 20($sp)
            lw $t4, 24($sp) # ptr
            
            sw $t0, 0($sp)
            subi $sp, $sp, 4
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            sw $t2, 0($sp)
            subi $sp, $sp, 4
            sw $t3, 0($sp)
            subi $sp, $sp, 4
            
            lw $t1, 4($t4)
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            lw $t1, 8($t4)
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            lw $t1, 12($t4)
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            lw $t1, 16($t4)
            sw $t1, 0($sp)
            subi $sp, $sp, 4
            jal fn_rect_collide
            addi $sp, $sp, 4
            lw $t0, 0($sp) # t0 = if wall collided with rect
            
            bne $t0, 1, ENDIF_collide_level_4
            IF_collide_level_4:
                # return
                lw $t3, 24($sp)
                
                addi $sp, $sp, 28 # // Pop local vars
                addi $sp, $sp, 4
                lw $ra, 0($sp)
                
                sw $t3, 0($sp)
                subi $sp, $sp, 4
                li $t1, 2
                sw $t1, 0($sp)
                subi $sp, $sp, 4
                jr $ra
            ENDIF_collide_level_4:
            
            # i++
            lw $t1, 28($sp)
            addi $t1, $t1, 1
            sw $t1, 28($sp)
            
        ENDIF_collide_level_2:
    
        # ptr += sizeof(Rect) // 20
        lw $t1, 24($sp)
        addi $t1, $t1, 20
        sw $t1, 24($sp)
    
        j LOOP_collide_level_2
    ENDLOOP_collide_level_2:
    
    
    
    
    lw $t0, 8($sp)
    lw $t1, 12($sp)
    lw $t2, 16($sp)
    lw $t3, 20($sp)
    
    sw $t0, 0($sp)
    subi $sp, $sp, 4
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    sw $t2, 0($sp)
    subi $sp, $sp, 4
    sw $t3, 0($sp)
    subi $sp, $sp, 4
    
    li $t1, 50
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 110
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 25
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 3
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    jal fn_rect_collide
    addi $sp, $sp, 4
    lw $t0, 0($sp) 
    sw $t0, 28($sp) # i = did paddle collide
    
    
    
    lw $t0, 8($sp)
    lw $t1, 12($sp)
    lw $t2, 16($sp)
    lw $t3, 20($sp)
    
    sw $t0, 0($sp)
    subi $sp, $sp, 4
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    sw $t2, 0($sp)
    subi $sp, $sp, 4
    sw $t3, 0($sp)
    subi $sp, $sp, 4
    
    li $t1, 60
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 102
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 3
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 3
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    jal fn_rect_collide
    addi $sp, $sp, 4
    lw $t0, 0($sp) # t0 = did ball collide
    
    lw $t1, 28($sp) # t1 = did paddle collide
    
    or $t0, $t0, $t1 # if (ball collide or paddle collide)
    bne $t0, 1, ENDIF_collide_level_5
    IF_collide_level_5:
        # return (0, 3)    
        addi $sp, $sp, 28 # // Pop local vars
        addi $sp, $sp, 4
        lw $ra, 0($sp)
        
        li $t1, 0
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        li $t1, 3
        sw $t1, 0($sp)
        subi $sp, $sp, 4
        jr $ra
    
    ENDIF_collide_level_5:
    
    # return (0, 4)    
    addi $sp, $sp, 28 # // Pop local vars
    addi $sp, $sp, 4
    lw $ra, 0($sp)
    
    li $t1, 0
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    li $t1, 4
    sw $t1, 0($sp)
    subi $sp, $sp, 4
    jr $ra


# Given two points, compute the top left and width height of the corresponding rect
fn_corners_to_rect: # (Vec corner1, Vec corner2) -> Vec t_l, Vec w_h
    addi $sp, $sp, 4
    lw $t3, 0($sp) # t3 = corner2.y
    addi $sp, $sp, 4
    lw $t2, 0($sp) # t2 = corner2.x
    addi $sp, $sp, 4
    lw $t1, 0($sp) # t1 = corner1.y
    addi $sp, $sp, 4
    lw $t0, 0($sp) # t0 = corner1.x
    
    # t4 = min x, t5 = max x
    bge $t0, $t2, ELSE_corners_to_rect_1
    IF_corners_to_rect_1: # if (t0 < t2)
        # t4 = t0, t5 = t2
        addi $t4, $t0, 0
        addi $t5, $t2, 0
    
        j ENDIF_corners_to_rect_1
    ELSE_corners_to_rect_1: # else: // t0 >= t2
        # t4 = t2, t5 = t0
        addi $t4, $t2, 0
        addi $t5, $t0, 0
        
    ENDIF_corners_to_rect_1:
    
    # t6 = min y, t7 = max y
    bge $t1, $t3, ELSE_corners_to_rect_2
    IF_corners_to_rect_2: # if (t1 < t3)
        # t6 = t1, t7 = t3
        addi $t6, $t1, 0
        addi $t7, $t3, 0
    
        j ENDIF_corners_to_rect_2
    ELSE_corners_to_rect_2: # else: // t1 >= t3
        # t6 = t3, t7 = t1
        addi $t6, $t3, 0
        addi $t7, $t1, 0
        
    ENDIF_corners_to_rect_2:
    
    # t4 = min x, t5 = max x
    # t6 = min y, t7 = max y
    
    sub $t5, $t5, $t4 # t5 = max x - min x = w
    sub $t7, $t7, $t6 # t6 = max y - min y = h
    
    # return
    sw $t4, 0($sp)
    subi $sp, $sp, 4
    sw $t6, 0($sp)
    subi $sp, $sp, 4
    sw $t5, 0($sp)
    subi $sp, $sp, 4
    sw $t7, 0($sp)
    subi $sp, $sp, 4
    
    jr $ra

